import React, { useState, useEffect } from "react";
import { useParams } from "react-router-dom";
import { Col, Container, Row } from "react-bootstrap";

const PostDetail = () => {
  const [postDetail, setPostDetail] = useState(null); // Handle as a single object
  const { id } = useParams();

  useEffect(() => {
    fetch(`http://localhost:9999/post/${id}`)
      .then((res) => res.json())
      .then((data) => {
        // Assuming `data` is an object and has a `reactions` field
        const postWithReactions = {
          ...data,
          likes: data.reactions.likes,
          dislikes: data.reactions.dislikes,
          liked: false,
          disliked: false,
        };
        setPostDetail(postWithReactions);
      })
      .catch((error) => console.log(error));
  }, [id]);

  if (!postDetail) return <div>Loading...</div>;

  return (
    <Container>
      <Row>
        <Col md={2}></Col>
        <Col md={8}>
          <Row>
            <Col md={12}>
              <h5>{postDetail.communityName}</h5>
              <h6>{postDetail.title}</h6>
              <p>{postDetail.body}</p>
              <p>Likes: {postDetail.likes} | Dislikes: {postDetail.dislikes}</p>
            </Col>
          </Row>
        </Col>
        <Col md={2}></Col>
      </Row>
    </Container>
  );
};

export default PostDetail;
