import React, { useState, useEffect } from "react";
import { Link, useParams } from "react-router-dom";
import { Button, Col, Container, Image, Row } from "react-bootstrap";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import {
  faThumbsUp,
  faThumbsDown,
  faClock,
} from "@fortawesome/free-solid-svg-icons";

import img1 from "../images/postImage/images_postId1.jpg";
import img2 from "../images/postImage/images_postId2.jpg";
import img3 from "../images/postImage/images_postId3.jpg";
import img4 from "../images/postImage/images_postId4.jpg";
import img5 from "../images/postImage/images_postId5.jpg";
import img6 from "../images/postImage/images_postId6.jpg";
import img7 from "../images/postImage/images_postId7.jpg";
import img8 from "../images/postImage/images_postId8.jpg";
import img9 from "../images/postImage/images_postId9.jpg";
import img10 from "../images/postImage/images_postId10.jpg";

const PostDetail = () => {
  const [postDetail, setPostDetail] = useState({
    id: "",
    title: "",
    body: "",
    reactions: {
      likes: "",
      dislikes: "",
    },
    comment: "",
    timeCreate: "",
    userName: "",
    communityName: "",
  });
  const { id } = useParams();

  useEffect(() => {
    fetch(`http://localhost:9999/post/${id}`)
      .then((res) => res.json())
      .then((data) => {
        const postWithReactions = {
          ...data,
          likes: data.reactions.likes,
          dislikes: data.reactions.dislikes,
          liked: false,
          disliked: false,
        };
        setPostDetail(postWithReactions);
      })
      .catch((error) => console.log(error));
  }, [id]);

  const images = [img1, img2, img3, img4, img5, img6, img7, img8, img9, img10];

  return (
    <Container>
      <Row>
        <Col md={2}>
          <Link to={"/"}>
            <Button
              className="btn btn-secondary mt-2"
              style={{ borderRadius: "10px", float: "right" }}
            >
              Back
            </Button>
          </Link>
        </Col>
        <Col md={8}>
          <Row>
            <Col md={12}>
              <Row className="d-flex">
                <Col md={3}>
                  <h5>{postDetail.communityName}</h5>
                </Col>
                <Col md={9} className="d-flex">
                  <Row>
                    <Col md={2}>
                      <FontAwesomeIcon icon={faClock} />
                    </Col>
                    <Col md={10}>
                      <p>{postDetail.timeCreate}</p>
                    </Col>
                  </Row>
                </Col>
              </Row>
            </Col>
          </Row>
          <Row>
            <Col md={12}>
              <h6>{postDetail.userName}</h6>
            </Col>
          </Row>
          <Row>
            <Col md={12}>
              <h2>{postDetail.title}</h2>
            </Col>
          </Row>
          <Row>
            <Col md={12}>
              <p>{postDetail.body}</p>
            </Col>
          </Row>
          <Row>
            <Col md={12}>
              <Image
                src={images[id - 1]}
                fluid
                style={{ width: "100%", borderRadius: "10px" }}
              />
            </Col>
          </Row>
          <Row className="mt-3" style={{ margin: "0px 0px" }}>
            <Col
              md={2}
              className="d-flex align-items-center"
              style={{ backgroundColor: "#dddddd", borderRadius: "15px", marginRight: '20px' }}
            >
              <Row>
                <Col md={2} style={{ marginTop: "2.5px" }}>
                  <FontAwesomeIcon
                    icon={faThumbsUp}
                    // onClick={() => handleLike(index)}
                    style={{
                      cursor: "pointer",
                      color: postDetail.liked ? "blue" : "black",
                    }}
                  />
                </Col>
                <Col md={2} style={{ marginTop: "2.5px" }}>
                  <h5>{postDetail.likes}</h5>
                </Col>
              </Row>
              <Row>
                <Col md={2} style={{ marginTop: "4px" }}>
                  <FontAwesomeIcon
                    icon={faThumbsDown}
                    // onClick={() => handleDislike(index)}
                    style={{
                      cursor: "pointer",
                      color: postDetail.disliked ? "red" : "black",
                    }}
                  />
                </Col>
                <Col md={2} style={{ marginTop: "2.5px" }}>
                  <h5>{postDetail.dislikes}</h5>
                </Col>
              </Row>
            </Col>

            <Col md={3} style={{ backgroundColor: "#dddddd", borderRadius: "15px", marginRight: '20px' }}>
              <h5>{postDetail.comment} Comment</h5>
            </Col>

            <Col md={1} style={{ backgroundColor: "#dddddd", borderRadius: "15px", marginRight: '20px' }}>
              <h5>Share</h5>
            </Col>

            <Col md={1} style={{ backgroundColor: "#dddddd", borderRadius: "15px", marginRight: '20px' }}>
              <h5>Save</h5>
            </Col>

            <Col md={1} style={{ backgroundColor: "#dddddd", borderRadius: "15px", marginRight: '20px', width: '50%' }}>
              <h5>Report</h5>
            </Col>
          </Row>
        </Col>
        <Col md={2}></Col>
      </Row>
    </Container>
  );
};

export default PostDetail;
